import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Chart from './components/Chart';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
      stackedData:{},
    }
  }

  componentDidMount() {
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    const url = "https://69smoo2dc6.execute-api.us-east-1.amazonaws.com/api/ics";
    fetch(proxyurl + url)
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json,
          stackedData: {
            labels: ['test1', 'test2', 'test3', 'test4', 'test5'],
            datasets: [
              {
                label:'In Use',
                data: [1,2,3,4,5],
                backgroundColor: [
                  'rgba(200, 200, 200, .6)',
                ]
              },
              {
                label:'Available',
                data: [1,2,3,4,5],
                backgroundColor: [
                  'rgba(200, 200, 200, .6)',
                ]
              }
            ]
          }
        })
      });
  }

  render() {

    var { isLoaded, items } = this.state;

    if (!isLoaded) {
      return <div>Loading data...</div>;
    } else {
      return (
        <div className="App">
          <div class="info">
              {items.map(item => (
                <li key={item.id}>
                  {item["site_displayname"]} currently has {item["usage_total"] - item["usage_active"]} out of its
                  total {item["usage_total"]} computers available!
                </li>
              ))};
          </div>
          <Chart chartData={this.state.stackedData} />
        </div>
      );
    }
  }
}

export default App;
