import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Chart from './components/Chart';
import NavBar from 'react-bootstrap/NavBar';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      ics: [],
      ews: [],
      isLoaded: false,
      stackedICSData:{},
      stackedEWSData:{},
      test:""
    }
  }

  componentDidMount() {
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    const url = "https://69smoo2dc6.execute-api.us-east-1.amazonaws.com/api/ics";
    const url2 = "https://69smoo2dc6.execute-api.us-east-1.amazonaws.com/api/ews";
    Promise.all([fetch(proxyurl + url), fetch(proxyurl + url2)])
      .then(([res1, res2]) => Promise.all([res1.json(), res2.json()]))
      .then(([data1, data2]) => {
        var ics_actives = [];
        var ics_availables = [];
        var ics_names = [];
        data1.map(item => (
          ics_actives.push(item["usage_active"]),
          ics_availables.push(item["usage_total"] - item["usage_active"]),
          ics_names.push(item["site_displayname"])
        ));
        var ews_actives = [];
        var ews_availables = [];
        var ews_names = [];
        data2.map(item => (
          ews_actives.push(item["inusecount"]),
          ews_availables.push(item["machinecount"] - item["inusecount"]),
          ews_names.push(item["strlabname"])
        ));
        this.setState({
          isLoaded: true,
          ics: data1,
          ews: data2,
          stackedICSData: {
            labels: ics_names,
            datasets: [
              {
                label:'In Use',
                data: ics_actives,
                backgroundColor: 'rgba(200, 100, 100, .6)'
              },
              {
                label:'Available',
                data: ics_availables,
                backgroundColor: 'rgba(100, 200, 100, .6)'
              }
            ],
          },
          stackedEWSData: {
            labels: ews_names,
            datasets: [
              {
                label:'In Use',
                data: ews_actives,
                backgroundColor: 'rgba(200, 100, 100, .6)'
              },
              {
                label:'Available',
                data: ews_availables,
                backgroundColor: 'rgba(100, 200, 100, .6)'
              }
            ],
          }
        })
      });
  }

  render() {

    var { ics, ews, isLoaded, stackedICSData, stackedEWSData, test } = this.state;
    var stackedOptions = {
      scales: {
        xAxes: [{
          stacked: true ,
          ticks: {
            autoSkip: false
          }
        }],
        yAxes: [{ stacked: true }]
      },
    };
    if (!isLoaded) {
      return <div>Loading data...</div>;
    } else {
      return (
        <div className="App">
          <div class="App-header"></div>
          <div class="chart_title">ICS (Instructional Computing Services)</div>
          <div class="chart_container">
            <Chart chartData={this.state.stackedICSData} chartOptions={stackedOptions} />
          </div>
          <div class="chart_title">EWS (Engineering Workstations)</div>
          <div class="chart_container">
            <Chart chartData={this.state.stackedEWSData} chartOptions={stackedOptions} />
          </div>
        </div>
      );
    }
  }
}

export default App;
