import React, {Component} from 'react';

class LabForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {lab: 'coconut'};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({lab: event.target.lab});
  }

  handleSubmit(event) {
    alert('Your favorite flavor is: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Pick a lab to view daily trends for:
          <select lab={this.state.lab} onChange={this.handleChange}>
            <option lab="DCL L416">Grapefruit</option>
            <option lab="ECEB 2022">Lime</option>
            <option lab="EH 406B1">Coconut</option>
            <option lab="SIEBL 0218">Mango</option>
          </select>
        </label>
        <input type="submit" lab="Submit" />
      </form>
    );
  }
}

export default LabForm;
